mapper.select:
	select * 
	from (select row_number()over(partition by substr(rdate, 1, 8) order by rdate asc) as rnum,
	 	calSeq, manager, title, content, rdate, wdate, tag
     	from calendar 
     	where substr(rdate, 1, 6)=#{yyyymm} ) a , category c
	where rnum between 1 and 5 and a.tag = c.tagSeq;

select * 
from calendar cal, category c
where calSeq=#{calSeq} and cal.tag = c.tagSeq;

mapper.update:
update calendar
set title=#{title}, content=#{content}, rdate=#{rdate}, wdate=now(), tag=#{tag}
where calSeq=#{calSeq}
